import { DEC,INC } from "../constant"
const initialState = {
    data:0
}

const rootReducer = (state = [],action)=>{

    switch(action.type)
    {
        case DEC:
            var count = state.data - 1;
           return{
             ...state,
             data:count

           }
        case INC:
            var count = state.data + 1;
            return{
             ...state,
             data:count
            }

    }

}
export default rootReducer;
