import { DEC,INC } from "../constant"
const initialState = {
    data:0
}

const rootReducer = (state = initialState,action)=>{
    debugger

    switch(action.type){
        case DEC:

           return{

             data:state.data - 1

           }
        case INC:

            return{

             data:state.data + 1
            }
        default:
            return state;

    }

}
export default rootReducer;
